import React from 'react';
import Card from './Card';
import Axios from 'axios';
import { useEffect, useState } from 'react';

const Pixar = () => {

    const [pixars, setPixars] = useState([]);

    const URL = 'https://api.themoviedb.org/4/list/198?page=1&api_key=00fea5657aa3cb22f4915ca3e8c7b486&language=fr';

    const getPixars = () => {
        Axios
            .get(URL)
            .then(response => setPixars(response.data.results))
            .catch(error => console.log("err =>", error))
    };

    useEffect(() => {
        getPixars();
    }, [])

  return (
    <div className="container">
        {pixars.length > 0 ?
            <>
                {pixars.map((element, index) =>
                    <Card 
                        key={index}
                        id={element.id}
                        title={element.title}
                        rating={element.vote_average}
                        poster={element.poster_path}
                        date={element.release_date}
                    />)
                }
            </>
        :
        <div class="lds-spinner"><div></div><div></div><div></div><div></div><div></div><div></div><div></div><div></div><div></div><div></div><div></div><div></div></div>
        }
    </div>
  )
}

export default Pixar;
