import React, { useContext }  from 'react';
import Register from './Register';
import Menu from './Menu';
import { AppContext, MyContext } from './../store/AppContext';
import brightness from './../img/brightness.png';

const Home = () => {

  const { store, setStore } = useContext(MyContext);

  const changeTheme = () => {
    if(store.theme === "light"){
      setStore({...store, theme: "dark"});
    }
    else{
      setStore({...store, theme: "light"});
    }
  }

  return (

    <div className={store.theme === "light" ? "lightTheme" : "darkTheme"}>
      <button className="switchTheme" onClick={changeTheme}><img src={brightness} className="switch"></img></button>
      <Menu/>
    </div>
  )
}

export default Home;
