import { useState, useEffect } from "react";

export const useToken = () => {

    const [hasToken, setHasToken] = useState(false);

    useEffect (() => {

        const token = sessionStorage.getItem('token');

        if(token != null){
            setHasToken(true);
        };
    }, []);

    return hasToken;

}
