import React, { useContext }  from 'react';
import Register from './Register';
import Menu from './Menu';
import { AppContext, MyContext } from './../store/AppContext';

const Home = () => {

  const { store, setStore } = useContext(MyContext);

  const changeTheme = () => {
    if(store.theme === "light"){
      console.log('mon thème doit passer a dark');
      setStore({...store, theme: "dark"});
    }
    else{
      console.log('mon thème doit passer a light');
      setStore({...store, theme: "light"});
    }
  }

  return (

    <>
      <button onClick={changeTheme}>Change Theme</button>
      <h1>theme : {store.theme}</h1>
      <Menu className={store.theme === "light" ? "lightTheme" : "darkTheme"}/>
    </>
  )
}

export default Home;
