import { useState, useEffect } from "react";

export const useToken = () => {

    const [hasToken, setHasToken] = useState(false);
    const [token, setToken] = useState(null);

    const saveToken = (newToken) => {
        sessionStorage.setItem('token', newToken);
        setToken(newToken);
    }

    useEffect (() => {
        if(token != null){
            setHasToken(true);
        };
    }, [token]);

    return {
        hasToken: hasToken,
        setToken: saveToken
    }

}
