import React from 'react'

const Card = (props) => {

    const BASE_IMG_URL = "https://image.tmdb.org/t/p/original";

  return (
    <div>
        <h3>{props.title}</h3>
        <p>{props.desc}</p>
        <small>{props.date}</small>
        <img src={BASE_IMG_URL + props.poster}></img>
    </div>
  )
}

export default Card;
