import React from 'react';
import Axios from 'axios';
import { useEffect, useState } from 'react';

const Profile = () => {

    const [profile, setProfile] = useState({});

    const [showProfile, setShowProfile] = useState(true);

    const URL = 'https://api-ri7.herokuapp.com/api/users/profile';

    const getProfile = () => {
        Axios
            .get(URL,
            {
                headers:{
                    Authorization : `Bearer ${sessionStorage.getItem('token')}`
                }
            })
            .then(response => setProfile(response.data))
            .catch(error => console.log("err =>", error))
    }

    useEffect(() => {
        getProfile();
    }, [])

    const displayForm =  () => {
        setShowProfile(!showProfile);
    }

  return (
    <div className="profile">
    {showProfile ?
        <>
            <img src={profile.avatar} className="profilePic"></img>
            <h3>{profile.firstname} {profile.lastname}</h3>
            <p>{profile.email}</p>
            <p>{profile.city} {profile.postalCode}</p>
            <p>{profile.birthdate}</p>
        </>
        :
        <>
            <div>le form</div>
        </>
    }
    <button className="button" onClick={displayForm}>Editer mon profil</button>
    </div>
  )
}

export default Profile;
