import React from 'react';
import Axios from 'axios';
import { useEffect, useState } from 'react';
import Input from './Input';

const Profile = () => {

    const [profile, setProfile] = useState({});

    const [showProfile, setShowProfile] = useState(true);

    const [firstName, setFirstName] = useState(firstName);
    const [lastName, setLastName] = useState(lastName);
    const [email, setEmail] = useState(email);
    const [password, setPassword] = useState(password);
    const [city, setCity] = useState(city);
    const [postalCode, setPostalCode] = useState(postalCode);
    const [birthdate, setBirthdate] = useState(birthdate);
    const [avatar, setAvatar] = useState(avatar);

    const URL = 'https://api-ri7.herokuapp.com/api/users/profile';

    const getProfile = () => {
        Axios
            .get(URL,
            {
                headers:{
                    Authorization : `Bearer ${sessionStorage.getItem('token')}`
                }
            })
            .then(response => setProfile(response.data))
            .catch(error => console.log("err =>", error))
    }

    useEffect(() => {
        getProfile();
    }, [])

    const displayForm =  () => {
        setShowProfile(!showProfile);

        Axios
            .put(URL, {
                firstname: firstName,
                lastname: lastName,
                email: email,
                password: password,
                city: city,
                postalCode: postalCode,
                birthdate: birthdate,
                avatar: avatar
            })
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            })
    }

  return (
    <div className="profile">
    {showProfile ?
        <>
            <img src={profile.avatar} className="profilePic"></img>
            <h3>{profile.firstname} {profile.lastname}</h3>
            <p>{profile.email}</p>
            <p>{profile.city} {profile.postalCode}</p>
            <p>{profile.birthdate}</p>
        </>
        :
        <>
            <form>
            <Input 
                label={"Prénom"}
                id={"firstName"}
                type={"text"}
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
            />
            <Input 
                label={"Nom"}
                id={"lastName"}
                type={"text"}
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
            />
            <Input 
                label={"Email"}
                id={"email"}
                type={"text"}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <Input 
                label={"Mot de passe"}
                id={"password"}
                type={"password"}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <Input 
                label={"Ville"}
                id={"city"}
                type={"text"}
                value={city}
                onChange={(e) => setCity(e.target.value)}
            />
            <Input 
                label={"Code postal"}
                id={"postalCode"}
                type={"text"}
                value={postalCode}
                onChange={(e) => setPostalCode(e.target.value)}
            />
            <Input 
                label={"Date de naissance"}
                id={"birthdate"}
                type={"date"}
                value={birthdate}
                onChange={(e) => setBirthdate(e.target.value)}
            />
            <Input 
                label={"Avatar"}
                id={"avatar"}
                type={"text"}
                value={avatar}
                onChange={(e) => setAvatar(e.target.value)}
            />
            </form>
        </>
    }
    <button className="button" onClick={displayForm}>Editer mon profil</button>
    </div>
  )
}

export default Profile;
