import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { MyRoutes, Selection } from './Router';
import logo from '../img/video.png';
import { AppContext, MyContext } from './../store/AppContext';
import login from './../img/login.png';
import users from './../img/users.png';
import moon from './../img/half-moon.png';
import sun from './../img/sun.png';

const Header = (props) => {

  const { store, setStore } = useContext(MyContext);

  const changeTheme = () => {
    if(store.theme === "light"){
      setStore({...store, theme: "dark"});
    }
    else{
      setStore({...store, theme: "light"});
    }
  }

  return (
    <>
    <div className={store.theme === "light" ? "lightTheme" : "darkTheme"}>
      <header className="header">
        <img className="logoHeader" src={logo}></img>
        <div className="header-links">
        {
          store.isUserAuth ?
          <>
            <Link className="links" to={MyRoutes.HOME}>Accueil</Link>
            <Link className="links" to={MyRoutes.PROFILE}>Mon profil</Link>
            <Link className="links" to={MyRoutes.LOGOUT}>Se deconnecter</Link>
            {store.theme === "light" ?
              <div className="switchTheme" onClick={changeTheme}><img src={moon} className="switch"></img></div>
              :
              <div className="switchTheme" onClick={changeTheme}><img src={sun} className="switch"></img></div>
            }
          </>
          :
          <>
            <Link className="links" to={MyRoutes.LOGIN}><img src={login} className="icon"></img> Se connecter</Link>
            <Link className="links" to={MyRoutes.REGISTER}><img src={users} className="icon"></img> S'inscrire</Link>
          </>
        }
        </div>
      </header>
    </div>
    </>

  )
}

export default Header;
